INSERT INTO [dbo].[BudgetProjects] ([ProjectID], [CategoryID], [ProjectCode], [ProjectName], [CopyDescriptionToNewAFE], [ProjectDescription]) VALUES (18, 1, N'E044', N'E044 - Encroachment Removal', 0, N'This project is to remove or document encroachments on the Legacy/Eagle system. Total cost is estimated to be1.9 million. This is a long-term plan that will be accomplished over the course of several years.    2016 Plan is to continue removing and/or getting encroachment agreements in place along the Legacy and Eagle lines. Encroachments will be those encroachments that need to be moved as they are close enough to the pipeline to prevent maintenance from being performed or are on the ROW but are easily removed for maintenance activity.')
