INSERT INTO [dbo].[BudgetProjects] ([ProjectID], [CategoryID], [ProjectCode], [ProjectName], [CopyDescriptionToNewAFE], [ProjectDescription]) VALUES (353, 2, N'C305', N'C305 - 10 inch Motor Protection Relay Replacement', 0, N'In 2016, this will be a one year project to replace the motor protection relays in the medium voltage switchgear on the 10 inch line in the Houston and Arlington Districts. The existing electro-mechanical and early vintage solid state relays in the high voltage switchgear will be replaced with todays advanced solid state electronic motor protection relays.')
INSERT INTO [dbo].[BudgetProjects] ([ProjectID], [CategoryID], [ProjectCode], [ProjectName], [CopyDescriptionToNewAFE], [ProjectDescription]) VALUES (354, 2, N'C306', N'C306 - Capacitor Start to Soft Starter Replacement Greenville', 0, N': This is a one year project to replace the existing capacitor starting system with two (2) soft starter systems one for unit #2 and one for unit #3 4500HP main line pump motors.')
INSERT INTO [dbo].[BudgetProjects] ([ProjectID], [CategoryID], [ProjectCode], [ProjectName], [CopyDescriptionToNewAFE], [ProjectDescription]) VALUES (355, 2, N'C307', N'C307 - Replace MCC at Marathon Station', 0, N'Replace Outdoor MCC in Marathon tank farm area.  This project will replace this equipment with an MCC suitable for the area and with more effective arc flash protection.')
INSERT INTO [dbo].[BudgetProjects] ([ProjectID], [CategoryID], [ProjectCode], [ProjectName], [CopyDescriptionToNewAFE], [ProjectDescription]) VALUES (356, 2, N'C308', N'C308 - Upgrade East Chicago Service Entrance', 0, N'Replace plywood service support and leaning meter pole at East Chicago station.  This project will replace this equipment with a longer lasting material such as aluminum and replace or straighten the pole.')
INSERT INTO [dbo].[BudgetProjects] ([ProjectID], [CategoryID], [ProjectCode], [ProjectName], [CopyDescriptionToNewAFE], [ProjectDescription]) VALUES (357, 2, N'C309', N'C309 - Eighteen New Deionization Tanks for VFD Systems', 0, N'This is a onetime purchase of eighteen new Deionization tanks for the 12 VFD stations.')
