INSERT INTO [dbo].[BudgetProjects] ([ProjectID], [CategoryID], [ProjectCode], [ProjectName], [CopyDescriptionToNewAFE], [ProjectDescription]) VALUES (362, 2, N'C312', N'C312 - Transformer Sampling System', 0, N'In 2017, this would be the first year of a multiyear project to install Transformer sampling ports on the existing 4160X480 Transformers. There are 28 Stations and we will attempt to complete 6 each year.')
INSERT INTO [dbo].[BudgetProjects] ([ProjectID], [CategoryID], [ProjectCode], [ProjectName], [CopyDescriptionToNewAFE], [ProjectDescription]) VALUES (363, 2, N'C313', N'C313 - OMNI Communication Upgrade', 0, N'Replace serial communications to OMNI with ethernet communications.  Eliminates serial to ethernet communications equipment and reduces amount of equipment that can fail.  Would like to purchase one communications module to test in 2015.  I have some funds available.  Not sure of project cost but working on 2016-2017 required funds')
INSERT INTO [dbo].[BudgetProjects] ([ProjectID], [CategoryID], [ProjectCode], [ProjectName], [CopyDescriptionToNewAFE], [ProjectDescription]) VALUES (364, 2, N'C314', N'C314 - GE PLC Upgrades', 0, N'Upgrade GE PLC''s in the stations purchased from Citgo with newer hardware.  Existing 90-70 and 90-30 hardware is being phased out by GE.  Will need to replace existing PLC''s with another model or manufacturer.')
