INSERT INTO [dbo].[BudgetProjects] ([ProjectID], [CategoryID], [ProjectCode], [ProjectName], [CopyDescriptionToNewAFE], [ProjectDescription]) VALUES (366, 2, N'C316', N'C316 - Sump and Transmix Radar Gauge Upgrade', 0, N'Upgrade Saab radar gauges with newer model.  Saab Pro gauges were installed at all the new booster stations and a few existing stations, that hardware is no longer supported by Saab.  Saab has a replacement guage but modifications will be required to install. 2016 - first year of multiyear project.  Purchase and install new gauge to identify what will be required complete  the upgrade.')
