INSERT INTO [dbo].[BudgetProjects] ([ProjectID], [CategoryID], [ProjectCode], [ProjectName], [CopyDescriptionToNewAFE], [ProjectDescription]) VALUES (369, 2, N'C320', N'C320 - New Booster Station Upgrades', 0, N'Replace equipment and upgrade to latest design to reduce equipment malfunctions.')
