INSERT INTO [dbo].[BudgetProjects] ([ProjectID], [CategoryID], [ProjectCode], [ProjectName], [CopyDescriptionToNewAFE], [ProjectDescription]) VALUES (371, 2, N'C321', N'C321 - Oklahoma Condensate - OCC', 0, N'New business opportunity will origin condensate from the Cushing Area to Explorer''s Glenpool facility. Project will involve a new origin facility with full meter station, prover and building for control systems.  In addition, project will involve approximately 50 miles of pipeline from Cushing to Explorer''s Glenpool facility.  At Glenpool, project will consist of a delivery facility.   This effort is for detailed engineering design to provide Total Installed Cost (TIC) for Project Engineering Design, Construction, Construction Management & Inspection Services for Project.')
INSERT INTO [dbo].[BudgetProjects] ([ProjectID], [CategoryID], [ProjectCode], [ProjectName], [CopyDescriptionToNewAFE], [ProjectDescription]) VALUES (372, 2, N'C322', N'C322 - Melissa Extension Project - Grizzly', 0, N'New business opportunity that will origin refined products from Explorer''s Greenville area to Melissa. In order to facilitate the project, Explorer will build a new origin manifold and pumps at Greenville, in addition to approximately 40 miles of pipeline from Greenvile to Melissa.  At Melissa, Explorer will build a delievry facility with full meter station, prover and building for control systems.  This effort is for detailed engineering design to provide Total Installed Cost (TIC) for Project Engineering Design, Construction, Construction Management & Inspection Services for the Melissa Extension Project.')
INSERT INTO [dbo].[BudgetProjects] ([ProjectID], [CategoryID], [ProjectCode], [ProjectName], [CopyDescriptionToNewAFE], [ProjectDescription]) VALUES (373, 2, N'C323', N'C323 - Tank at Fauna', 0, N'Currently underutilizing a 120K barrel tank as transmix tank at Fauna Station.  This project is a two year project that will build 2 - 25K barrel tanks at Fauna - 1 tank in 2016 and the other in 2017.  The smaller tanks are comparable to the transmix tankage needs of about 20K barrels at Fauna.')
