INSERT INTO [dbo].[BudgetProjectJustifications] ([ProjectCostID], [IsSubmitted], [ProjectManager], [ProjectName], [Description], [Justification], [CreatedOn]) VALUES (9, 0, N'mshores', N'E020 - Tank Maintenance', N'Clean, inspect, repair, and perform maintenance on 18 product tanks.', N'Good Idea', '20151104')
INSERT INTO [dbo].[BudgetProjectJustifications] ([ProjectCostID], [IsSubmitted], [ProjectManager], [ProjectName], [Description], [Justification], [CreatedOn]) VALUES (10, 0, N'mshores', N'E022 - API 653\EPA\DOT Tank Inspections', N'Perform system wide Tank Inspections according to requirements of API 653, State EPA’s, and PHMSA.  This includes floating roof seals, external inspections of in-service tanks, periodic internal inspections, and new strapping tables when the tanks are out of service along with the annual DOT Breakout Tank Inspections.', N'Good Idea', '20151104')
INSERT INTO [dbo].[BudgetProjectJustifications] ([ProjectCostID], [IsSubmitted], [ProjectManager], [ProjectName], [Description], [Justification], [CreatedOn]) VALUES (11, 0, N'bdedeke', N'E030 - Erosion Repairs', N'Adequate cover over the pipeline will be re-established by refilling wash outs and areas of shallow cover on the pipeline right-of-way.  Various erosion control techniques will be implemented to manage current and prevent future water and wind erosion.  Evaluation is constantly underway to determine which sites can be repaired using conventional erosion control techniques. Depth of cover data  will be utilized to prevent future erosion.Depth of cover survey will be a 5 year project covering the entire system.', N'Good Idea', '20151104')
INSERT INTO [dbo].[BudgetProjectJustifications] ([ProjectCostID], [IsSubmitted], [ProjectManager], [ProjectName], [Description], [Justification], [CreatedOn]) VALUES (12, 0, N'rmatlock', N'E031 - River Crossing Inspections', N'Perform year 2016 river crossing inspections.   Flood events and three non-navigable $50,000   Other general inspection of river crossings.', N'Good Idea', '20151104')
INSERT INTO [dbo].[BudgetProjectJustifications] ([ProjectCostID], [IsSubmitted], [ProjectManager], [ProjectName], [Description], [Justification], [CreatedOn]) VALUES (13, 0, N'rmatlock', N'E032 - Air Patrol', N'Perform aerial patrol on all 1836 miles of the Explorer system using Helicopter Contractors for 5 segments of 673 miles of urban and congested right of way and Fixed Wing Contractors for 1,163 miles of rural right of way. All patrols are scheduled to be made weekly.   Additionally 18.75 miles are patrolled via foot every other week.   Perform any additional patrols upon request by Explorer personnel.', N'Good Idea', '20151104')
INSERT INTO [dbo].[BudgetProjectJustifications] ([ProjectCostID], [IsSubmitted], [ProjectManager], [ProjectName], [Description], [Justification], [CreatedOn]) VALUES (14, 0, N'rmatlock', N'E035 - ROW One Call Screenings', N'System wide One Call Screenings, State One Call Agency Fees and IRTHNet Ticket Management System', N'Good Idea', '20151104')
INSERT INTO [dbo].[BudgetProjectJustifications] ([ProjectCostID], [IsSubmitted], [ProjectManager], [ProjectName], [Description], [Justification], [CreatedOn]) VALUES (15, 0, N'rmatlock', N'E040 - Right-of-Way Marking/Clearing', N'Right of Way Maintenance and Marking Planned for 2016                   Explorer  District         Total Miles          Mowing          Road Crossings           Gulf Coast     396                     315                        81 Greenville     343                     135                      208 Arlington       287                      101                      186 Glenpool       306                      150                      156                                                                                         Wood River  439                     251                      188 Hammond      70                        70                           0   Total             1841                 1022                       819    Cross Country Mowing from Oklahoma state line to Illinois state line (28”), Plantersville to Hearne (10”).', N'Good Idea', '20151104')
INSERT INTO [dbo].[BudgetProjectJustifications] ([ProjectCostID], [IsSubmitted], [ProjectManager], [ProjectName], [Description], [Justification], [CreatedOn]) VALUES (16, 0, N'rmatlock', N'E041 - Special Clearing Systemwide', N'Special clearing of Right of Way by removing trees, side trimming, clearing over growth.', N'Good Idea', '20151104')
INSERT INTO [dbo].[BudgetProjectJustifications] ([ProjectCostID], [IsSubmitted], [ProjectManager], [ProjectName], [Description], [Justification], [CreatedOn]) VALUES (17, 0, N'rmatlock', N'E043 - Replace River Crossing Signs', N'Replace weathered and damaged River Crossing signs and support structures across the Explorer Pipeline System.', N'Good Idea', '20151104')
INSERT INTO [dbo].[BudgetProjectJustifications] ([ProjectCostID], [IsSubmitted], [ProjectManager], [ProjectName], [Description], [Justification], [CreatedOn]) VALUES (18, 0, N'rmatlock', N'E044 - Encroachment Removal', N'This project is to remove or document encroachments on the Legacy/Eagle system. Total cost is estimated to be1.9 million. This is a long-term plan that will be accomplished over the course of several years.    2016 Plan is to continue removing and/or getting encroachment agreements in place along the Legacy and Eagle lines. Encroachments will be those encroachments that need to be moved as they are close enough to the pipeline to prevent maintenance from being performed or are on the ROW but are easily removed for maintenance activity.', N'Good Idea', '20151104')
INSERT INTO [dbo].[BudgetProjectJustifications] ([ProjectCostID], [IsSubmitted], [ProjectManager], [ProjectName], [Description], [Justification], [CreatedOn]) VALUES (19, 0, N'astacy', N'E050 - Technical, Leadership, Other Training', N'Company training for Regulatory, Succession, Leadership, Destination Leadership and Employees (IDPs).', N'Good Idea', '20151104')
INSERT INTO [dbo].[BudgetProjectJustifications] ([ProjectCostID], [IsSubmitted], [ProjectManager], [ProjectName], [Description], [Justification], [CreatedOn]) VALUES (20, 0, N'dyanchunas', N'E051 - Regulatory  Training', N'Company training for compliance with regulatory requirements for OSHA, EPA, and other agencies.  This is for the following areas: Gulf Coast, Arlington, Greenville, Glenpool, Tulsa office, Wood River, and Hammond.', N'Good Idea', '20151104')
INSERT INTO [dbo].[BudgetProjectJustifications] ([ProjectCostID], [IsSubmitted], [ProjectManager], [ProjectName], [Description], [Justification], [CreatedOn]) VALUES (21, 0, N'astacy', N'E052 - Employee Training Summit', N'Similar to previous BUCK Summit…this employee training summit will provide the opportunity to conduct required training (ethics, diversity, HR related compliance) with all employees as well as leadership and team building opportunities offsite.  Will take place at Big Cedar.', N'Good Idea', '20151104')
INSERT INTO [dbo].[BudgetProjectJustifications] ([ProjectCostID], [IsSubmitted], [ProjectManager], [ProjectName], [Description], [Justification], [CreatedOn]) VALUES (22, 0, N'rmoore', N'E053 - Remediation', N'This project entails continuation of spill site remediation efforts at the following locations: Palestine, Rochelle Blvd., Holdenville, MP403 Wood River, Navasota, Arlington Station, Road to Six Flags, MP175 Walnut Grove/Springfield, Warschun Road, MP398 Coleman, Waco Terminal, MP179 Conroe Station, Port Arthur from girth weld crack, U.S. Oil Recovery superfund, MP8.9 Port Arthur, MP490 Decatur, IL and Wood River TMX sump release.', N'Good Idea', '20151104')
INSERT INTO [dbo].[BudgetProjectJustifications] ([ProjectCostID], [IsSubmitted], [ProjectManager], [ProjectName], [Description], [Justification], [CreatedOn]) VALUES (23, 0, N'stollette', N'E054 - NEC Compliance Modifications', N'This is the ninth year of a multi-year project to inspect and correct possible discrepancies in the electrical systems at Explorer’s facilities in order to ensure that they are compliance with the National Electric Code.  This project is outsourced for installation consistency and to reduce downtime of facilities. The Company has added review and maintenance of area classification drawings for Explorer Stations to identify the electrical classification at each station to assist engineering and field personnel in selecting the correct electrical equipment for the different area in the stations.', N'Good Idea', '20151104')
INSERT INTO [dbo].[BudgetProjectJustifications] ([ProjectCostID], [IsSubmitted], [ProjectManager], [ProjectName], [Description], [Justification], [CreatedOn]) VALUES (24, 0, N'rmatlock', N'E056 - ROW Notification/Stakeholder Mailout', N'Print and distribute safety brochures for targeted mailings to educate and inform the Affected Public, Public Officials, Emergency Responders and Excavators.  Participate in other Mass Media campaigns along our ROW.', N'Good Idea', '20151104')
INSERT INTO [dbo].[BudgetProjectJustifications] ([ProjectCostID], [IsSubmitted], [ProjectManager], [ProjectName], [Description], [Justification], [CreatedOn]) VALUES (25, 0, N'churst', N'E058 - Pipeline Remediation', N'The inspection estimates are based on anomaly inspection and repair portion of the integrity management program.   The project costs is to excavate, analyze, and recoat and/or repair both actionable and Explorer criteria anomalies identified by ILI tool runs.', N'Good Idea', '20151104')
INSERT INTO [dbo].[BudgetProjectJustifications] ([ProjectCostID], [IsSubmitted], [ProjectManager], [ProjectName], [Description], [Justification], [CreatedOn]) VALUES (26, 0, N'bdedeke', N'E059 - Internal Inspection', N'The inspection estimates are based on the assessment portion of the integrity management program.  The project costs include $1,000,000 to perform internal line inspections and $100,000 for contract report validation.  Line segments to be assessed include: 10'' Washburn to Plantersville 10'' Plantersville to Hearne Pump 10'' Hearne Pump to Ross 10'' Ross to Arlington 8'' Grapevine Meter to DFW Airport 24'' Wood River to Hammond 24” Schererville Junction to Griffith Meter Station', N'Good Idea', '20151104')
INSERT INTO [dbo].[BudgetProjectJustifications] ([ProjectCostID], [IsSubmitted], [ProjectManager], [ProjectName], [Description], [Justification], [CreatedOn]) VALUES (27, 0, N'bdedeke', N'E060 - Risk Mitigation Projects', N'Technical studies and analysis of specific integrity threats including;  Continued evaluation and analysis of lack of fusion defects in ERW pipe Failure analysis DNV Reports (Assessments) Corrosion Growth Studies', N'Good Idea', '20151104')
INSERT INTO [dbo].[BudgetProjectJustifications] ([ProjectCostID], [IsSubmitted], [ProjectManager], [ProjectName], [Description], [Justification], [CreatedOn]) VALUES (28, 0, N'jvanschenck', N'E061 - Asset Integrity Risk Assessment', N'This project is also known as '' E061 – Asset Integrity Risk Assessment'', combined with E063 - Integrity Risk Model This project combines previous HCA efforts and risk modelling efforts for continual improvement.  The scope of this project:   Includes the prioritization of risk mitigation projects Includes support of pipeline and facility risk threat and consequence assessments Further implements data collection efforts on Explorer''s mainline system to refine and more accurately catalog unusually sensitive areas (USAs)', N'Good Idea', '20151104')
INSERT INTO [dbo].[BudgetProjectJustifications] ([ProjectCostID], [IsSubmitted], [ProjectManager], [ProjectName], [Description], [Justification], [CreatedOn]) VALUES (29, 0, N'bdedeke', N'E062 - Hydrostatic Testing', N'A multiyear project to perform hydrostatic tests on currently unpiggable delivery lines as scheduled in the continuing integrity assessment process or hydrostatic tests to assess for seam related issues.  The lines scheduled for required hydrostatic tests are:    ·        16” Griffith Meter to Buckeye X104 West line (Distillate) ·         16” Griffith Meter to Buckeye X105 East line (Gasoline) ·         12/16” Hammond Tank Farm to Wolverine ·         14” East Chicago Meter to Clark ·         12” East Chicago Meter to Badger (Mobil) Junction', N'Good Idea', '20151104')
INSERT INTO [dbo].[BudgetProjectJustifications] ([ProjectCostID], [IsSubmitted], [ProjectManager], [ProjectName], [Description], [Justification], [CreatedOn]) VALUES (30, 0, N'jvanschenck', N'E063 - Integrity Risk Model', N'This project merged into E061 - 5/26/2015  In 2014, Explorer ended use of risk modelling software that was experiencing expertise and support shortcomings in the pipeline industry.  Asset Integrity changed platforms to an in-house solution that provides more scalability and enterprise reporting capabilities.  This project will support consulting services to expand reporting to show risk drivers and additional information to help make decisions on prioritizing operations and maintenance work.', N'Good Idea', '20151104')
INSERT INTO [dbo].[BudgetProjectJustifications] ([ProjectCostID], [IsSubmitted], [ProjectManager], [ProjectName], [Description], [Justification], [CreatedOn]) VALUES (31, 0, N'bcowan', N'E064 - Tank Dike Repairs', N'This project is a Multi-year plan to repair settlement and erosion on the tank dikes at Hammond, Wood River, Grapevine, Houston, Fauna and Port Arthur Stations.  The tank dikes’ heights will be restored to design heights and dressed to match current dikes.  Survey work was done on Greenville, Port Arthur and Fauna in 2014.  These dollars are planned to be used to upgrade Wood River.', N'Good Idea', '20151104')
INSERT INTO [dbo].[BudgetProjectJustifications] ([ProjectCostID], [IsSubmitted], [ProjectManager], [ProjectName], [Description], [Justification], [CreatedOn]) VALUES (32, 0, N'rmatlock', N'E065 - D.O.T. Public Education', N'Public Awareness and Emergency Responder meetings in order to inform and educate the general public about Explorer Pipeline’s commitment to safety and environmental protection.  The monies will also be used to distribute activity kits to school aged children as well as handouts and/or posters to various organizations.  This AFE is also used for miscellaneous internal and external handouts for various company related activities and emergency responder meetings', N'Good Idea', '20151104')
INSERT INTO [dbo].[BudgetProjectJustifications] ([ProjectCostID], [IsSubmitted], [ProjectManager], [ProjectName], [Description], [Justification], [CreatedOn]) VALUES (33, 0, N'rmitchener', N'E066 - Expense Tools & Test Equipment', N'Purchase replacement tools and test equipment for EMT’s.   Pt. Arthur, Houston, Greenville, Arlington, Glenpool, Wood River, Hammond', N'Good Idea', '20151104')
