INSERT INTO [dbo].[BudgetProjectJustifications] ([ProjectCostID], [IsSubmitted], [ProjectManager], [ProjectName], [Description], [Justification], [CreatedOn]) VALUES (35, 0, N'rmatlock', N'E070 - Public Awareness Survey (API 1162)', N'This Nationwide API survey promotes the public’s awareness of liquid pipelines.', N'Good Idea', '20151104')
INSERT INTO [dbo].[BudgetProjectJustifications] ([ProjectCostID], [IsSubmitted], [ProjectManager], [ProjectName], [Description], [Justification], [CreatedOn]) VALUES (36, 0, N'kbrown', N'E071 - Emergency Response Notification', N'Notification programs and on-line emergency response portal systems with Explorer emergency information for emergency responders that would respond to an emergency along the pipeline system.  System will satisfy the DOT PHMSA following the advisory they issued from an incident in California.', N'Good Idea', '20151104')
INSERT INTO [dbo].[BudgetProjectJustifications] ([ProjectCostID], [IsSubmitted], [ProjectManager], [ProjectName], [Description], [Justification], [CreatedOn]) VALUES (37, 0, N'stollette', N'E072 - Electrical & Arc Flash Related Procedures & Training', N'Continue to develop electrical procedures in accordance with Explorer’s electrical safety program and Arc Flash labels.  Train employees on the newly-developed procedures.  This training would occur in the Port Arthur, Houston, Arlington, Greenville, Glenpool, Wood River, Hammond and Tulsa areas.', N'Good Idea', '20151104')
INSERT INTO [dbo].[BudgetProjectJustifications] ([ProjectCostID], [IsSubmitted], [ProjectManager], [ProjectName], [Description], [Justification], [CreatedOn]) VALUES (38, 0, N'stollette', N'E073 - Arc Flash Labeling 12" 24" 28" Lines', N'This project to provide arc flash and electrical safety labeling for various locations in the Explorer system.  In the period between 2010 and 2013, arc flash studies were completed and arc flash labels were install on all required electrical equipment in Explorer’s system.  In 2014, the initial arc flash study performed previously was updated.  Beginning in 2015, the project transitioned into a yearly update cycle for twenty facilities each year.     In 2016, we will perform Arc Flash study and install Arc Flash Labels for various locations.  Twenty (20) locations will be included in the Greenville and Glenpool districts.', N'Good Idea', '20151104')
INSERT INTO [dbo].[BudgetProjectJustifications] ([ProjectCostID], [IsSubmitted], [ProjectManager], [ProjectName], [Description], [Justification], [CreatedOn]) VALUES (39, 0, N'kbrown', N'E074 - DOT Manual Revisions', N'Update and provide administrative assistance updating the required DOT PHMSA operations, maintenance and emergency manuals for Explorer.', N'Good Idea', '20151104')
INSERT INTO [dbo].[BudgetProjectJustifications] ([ProjectCostID], [IsSubmitted], [ProjectManager], [ProjectName], [Description], [Justification], [CreatedOn]) VALUES (40, 0, N'rmartin', N'E076 - As-Built Drafting Carryover', N'Many expense AFE projects are completed each year and the AFE is closed. The as-built documentation, data and drafting is not completed before that AFE is closed. The work must be completed to support PHMSA compliance requirements the following year and charged against overhead instead of capturing the charges to an AFE.', N'Good Idea', '20151104')
