INSERT INTO [dbo].[BudgetProjectJustifications] ([ProjectCostID], [IsSubmitted], [ProjectManager], [ProjectName], [Description], [Justification], [CreatedOn]) VALUES (96, 0, N'rwoodford', N'C026 - Autosamplers', N'N/A', N'Good Idea', '20151104')
