INSERT INTO [dbo].[BudgetProjectJustifications] ([ProjectCostID], [IsSubmitted], [ProjectManager], [ProjectName], [Description], [Justification], [CreatedOn]) VALUES (98, 0, N'ssansone', N'C038 - Drain Valve Refurbish', N'Send drain valves that are removed from our stations to repair facilities for repair and testing.  Valves may come from the Port Arthur, Houston, Greenville, Glenpool, Arlington, Wood River or Hammond areas as replacements are made by field personnel.', N'Good Idea', '20151104')
INSERT INTO [dbo].[BudgetProjectJustifications] ([ProjectCostID], [IsSubmitted], [ProjectManager], [ProjectName], [Description], [Justification], [CreatedOn]) VALUES (99, 0, N'lmcneely', N'C039 - Replacement Pump Couplings', N'This is a multi-year project to replace existing gear type pump/motor couplings with disk pack couplings at original mainline pump stations in order to have one motor hub design for all interchangeable HP motors so that spare motors can readily be installed.     Due to recent issues with the gear driven couplings, profiled hubs and components have been ordered for the mainline #1 units from Pasadena (unit #3) to Holdenville along with a complete coupling for Durant #1 due to an on-demand issue there.  This is to expedite replacement of the older gear driven style couplings with newer, lighter and more efficient Metastream TLC couplings.', N'Good Idea', '20151104')
INSERT INTO [dbo].[BudgetProjectJustifications] ([ProjectCostID], [IsSubmitted], [ProjectManager], [ProjectName], [Description], [Justification], [CreatedOn]) VALUES (100, 0, N'bjones', N'C040 - Explorer 10 Inch Motor Replacement', N'Company would start a multiyear project to replace motors on the 10” line.  This project would be done in the Houston and Arlington District.', N'Good Idea', '20151104')
INSERT INTO [dbo].[BudgetProjectJustifications] ([ProjectCostID], [IsSubmitted], [ProjectManager], [ProjectName], [Description], [Justification], [CreatedOn]) VALUES (101, 0, N'stollette', N'C041 - Explorer 10 Inch PQM for Power Monitoring', N'This project will add a Multilin PQMII Power Quality Monitor to each of the pump stations on the Explorer 10” pipeline to allow the displaying of real time demand usage at these stations on the CCT’s screen in the Control Center.  This will allow the CCT’s to have real time demand data available to them to use to operate the pipeline systems.  This work would be performed in the Houston and Arlington Districts.', N'Good Idea', '20151104')
INSERT INTO [dbo].[BudgetProjectJustifications] ([ProjectCostID], [IsSubmitted], [ProjectManager], [ProjectName], [Description], [Justification], [CreatedOn]) VALUES (102, 0, N'ghoelscher', N'C043 - Hydraulic Leak Detection System', N'Modify station hydraulic skids with additional instrumentation and controls to detect hydraulic line failure and isolate system to prevent additional loss of hydraulic fluid. Area''s - Pt. Arthur, Houston, Greenville, Glenpool, Wood River, Hammond', N'Good Idea', '20151104')
INSERT INTO [dbo].[BudgetProjectJustifications] ([ProjectCostID], [IsSubmitted], [ProjectManager], [ProjectName], [Description], [Justification], [CreatedOn]) VALUES (103, 0, N'ghoelscher', N'C042 - New Booster Station Critical Loads', N'Modify New Booster Station Motor Control Center (MCC), Essential Services Section, to allow additional critical loads such as HVAC systems and station outdoor lighting. Pt. Arthur, Houston, Greenville, Glenpool, Wood River', N'Good Idea', '20151104')
INSERT INTO [dbo].[BudgetProjectJustifications] ([ProjectCostID], [IsSubmitted], [ProjectManager], [ProjectName], [Description], [Justification], [CreatedOn]) VALUES (104, 0, N'ghoelscher', N'C046 - Pasadena Storm Water Backup', N'Provide backup system to allow pumping of rain/storm water out of Pasadena Station during storms when power outages occur at Pasadena Station.', N'Good Idea', '20151104')
INSERT INTO [dbo].[BudgetProjectJustifications] ([ProjectCostID], [IsSubmitted], [ProjectManager], [ProjectName], [Description], [Justification], [CreatedOn]) VALUES (105, 0, N'bjones', N'C044 - Generator Transfer Switch', N'This is the third year of a multi-year project to replace secondary protection on the 480 volt transformers and add a manual transfer switch to allow ease of connection for generator services at the tank farm and other facilities of Explorer Pipeline.  This project provides for installation of the transfer switches in the Glenpool and Wood River districts in 2016.', N'Good Idea', '20151104')
INSERT INTO [dbo].[BudgetProjectJustifications] ([ProjectCostID], [IsSubmitted], [ProjectManager], [ProjectName], [Description], [Justification], [CreatedOn]) VALUES (106, 0, N'bgasperino', N'C045 - Leak Detection Metering Systems', N'Add additional instrumentation and metering to the pipeline systems to provide additional data to the SCADA Leak Detection system for improved accuracy.', N'Good Idea', '20151104')
INSERT INTO [dbo].[BudgetProjectJustifications] ([ProjectCostID], [IsSubmitted], [ProjectManager], [ProjectName], [Description], [Justification], [CreatedOn]) VALUES (107, 0, N'tjohanson', N'C053 - Wood River Oil Water Separator', N'Terracon did a feasibility study in 2014 and recommended a carbon filter system to replace the existing oily water sewer system at Wood River.  $350,000 of the $400,000 budgeted in 2014 will be carried over; this additional $150,000 will be needed to complete the installation of the system.', N'Good Idea', '20151104')
INSERT INTO [dbo].[BudgetProjectJustifications] ([ProjectCostID], [IsSubmitted], [ProjectManager], [ProjectName], [Description], [Justification], [CreatedOn]) VALUES (108, 0, N'bjones', N'C059 - 5KV Breaker Replacements', N'Replace 5KV switchgear vacuum breaker assembly with new assembly that uses vacuum breaker/starters at the non-VFD station on the 28” and 24” lines.', N'Good Idea', '20151104')
INSERT INTO [dbo].[BudgetProjectJustifications] ([ProjectCostID], [IsSubmitted], [ProjectManager], [ProjectName], [Description], [Justification], [CreatedOn]) VALUES (109, 0, N'bjones', N'C060 - Sump Pump Motor Upgrades', N'In 2017, this would be the first year of a three year program to purchase and install new motors for the sump pumps at various locations.  In 2017, this project will be performed in the Port Arthur, Houston and Greenville districts.', N'Good Idea', '20151104')
INSERT INTO [dbo].[BudgetProjectJustifications] ([ProjectCostID], [IsSubmitted], [ProjectManager], [ProjectName], [Description], [Justification], [CreatedOn]) VALUES (110, 0, N'crose', N'C072 - 10 Inch System Block Valve Replacement', N'Purchase and install replacement block valves for the 10 inch system.  This includes locations in the Houston and Arlington Areas.', N'Good Idea', '20151104')
INSERT INTO [dbo].[BudgetProjectJustifications] ([ProjectCostID], [IsSubmitted], [ProjectManager], [ProjectName], [Description], [Justification], [CreatedOn]) VALUES (111, 0, N'bcowan', N'C074 - TMX Containment Legacy Stations', N'PHMSA is requiring all Transmix Tanks at the Legacy Booster Stations to have containment structures constructed around them to ensure containment of product in case of a release. This AFE covers containment construction for three (3) Booster Stations – Durant (Greenville District), Palestine (Greenville District) & Huntsville (Houston District). Booster Stations previously addressed in 2013 & 2014 – Decatur, Gerald, Lebanon, Mt. Vernon, Vinita & Holdenville.', N'Good Idea', '20151104')
INSERT INTO [dbo].[BudgetProjectJustifications] ([ProjectCostID], [IsSubmitted], [ProjectManager], [ProjectName], [Description], [Justification], [CreatedOn]) VALUES (112, 0, N'jduvall', N'C076 - Line Contactor for 4160 Volt Fused Disconnects', N'In 2016 this would be the second year of a six year project to install line contactor for 4160 volt fused disconnects. This work will be performed in the Port Arthur and Houston districts.', N'Good Idea', '20151104')
