INSERT INTO [dbo].[BudgetProjectJustifications] ([ProjectCostID], [IsSubmitted], [ProjectManager], [ProjectName], [Description], [Justification], [CreatedOn]) VALUES (115, 0, N'crose', N'C084 - 24" Block Valve Replacement', N'This is a multi-year project to refurbish and replace 24 inch valves along the mainline system.  In the initial year, 4 valves were purchased; two will be installed in 2015, and two will be held in reserve.  In subsequent years, removed valves will be refurbished and rotated throughout the line.', N'Good Idea', '20151104')
