INSERT INTO [dbo].[BudgetProjectJustifications] ([ProjectCostID], [IsSubmitted], [ProjectManager], [ProjectName], [Description], [Justification], [CreatedOn]) VALUES (135, 0, N'tgolla', N'C115 - Security System Improvements', N'This project funds a multiyear effort to improve security throughout Explorer locations.  With the successful implementation of Cellular communications to the field we can now implement IP cameras at field locations to improve security and visibility into the sites.  The funds in this project will support the deployment of additional IP cameras in the field for remote monitoring and the expansion of our current cardreader system to field locations.', N'Good Idea', '20151104')
INSERT INTO [dbo].[BudgetProjectJustifications] ([ProjectCostID], [IsSubmitted], [ProjectManager], [ProjectName], [Description], [Justification], [CreatedOn]) VALUES (136, 0, N'Christopher Meyer', N'C123 - Vapor Monitor Installation', N'Explorer utilizes vapor monitors at unmanned locations along the pipeline as a form of leak detection.  This project is to install vapor monitors at sites that currently do not have them.  In 2016 vapor monitors will be installed at Carrolton Junction.', N'Good Idea', '20151104')
