INSERT INTO [dbo].[BudgetProjectJustifications] ([ProjectCostID], [IsSubmitted], [ProjectManager], [ProjectName], [Description], [Justification], [CreatedOn]) VALUES (156, 0, N'rclegg', N'C159 - Sphere Bypass System Upgrades', N'Replace Allen-Bradley PLC2 and relay logic Sphere Bypass system with logic in the ControlLogix(PLC) at Houston, Palestine, Huntsville, Holdenville, Vinita, Mt Vernon, Gerald and Decatur.', N'Good Idea', '20151104')
