INSERT INTO [dbo].[BudgetProjectJustifications] ([ProjectCostID], [IsSubmitted], [ProjectManager], [ProjectName], [Description], [Justification], [CreatedOn]) VALUES (371, 0, N'pnwakoby', N'C321 - Oklahoma Condensate - OCC', N'New business opportunity will origin condensate from the Cushing Area to Explorer''s Glenpool facility. Project will involve a new origin facility with full meter station, prover and building for control systems.  In addition, project will involve approximately 50 miles of pipeline from Cushing to Explorer''s Glenpool facility.  At Glenpool, project will consist of a delivery facility.   This effort is for detailed engineering design to provide Total Installed Cost (TIC) for Project Engineering Design, Construction, Construction Management & Inspection Services for Project.', N'Good Idea', '20151104')
