INSERT INTO [dbo].[BudgetProjectJustifications] ([ProjectCostID], [IsSubmitted], [ProjectManager], [ProjectName], [Description], [Justification], [CreatedOn]) VALUES (373, 0, N'pnwakoby', N'C323 - Tank at Fauna', N'Currently underutilizing a 120K barrel tank as transmix tank at Fauna Station.  This project is a two year project that will build 2 - 25K barrel tanks at Fauna - 1 tank in 2016 and the other in 2017.  The smaller tanks are comparable to the transmix tankage needs of about 20K barrels at Fauna.', N'Good Idea', '20151104')
